export const getTodosState = (store) => store.todoes;

export const getTodoList = (store) =>
  getTodosState(store) ? getTodosState(store).allIds : [];

export const getTodoById = (store, id) =>
  getTodosState(store) ? { ...getTodosState(store).byIds[id], id } : {};

export const getTodos = (store) =>
  getTodoList(store).map((id) => getTodoById(store, id));

export const getVisibleTodos = (state) => {
  const { byIds, allIds, visibilityFilter } = state.todoes;
  const todos = allIds.map((id) => byIds[id]);
  switch (visibilityFilter) {
    case "SHOW_COMPLETED": {
      return todos.filter((todo) => todo.completed);
    }
    case "SHOW_ACTIVE": {
      return todos.filter((todo) => !todo.completed);
    }
    case "SHOW_ALL":
    default:
      return todos;
  }
};
import React from "react";
import { connect } from "react-redux";

const VisibilityFilter = ({ dispatch }) => {
  const handleVisibilityFilter = (filter) => {
    dispatch({ type: "SET_VISIBILITY_FIlTER", payload:filter });
  };
  return (
    <div>
      <h1>Visibility Filter</h1>
      <button
        onClick={() => {
          handleVisibilityFilter("SHOW_ALL");
        }}
      >
        Show All
      </button>
      <button
        onClick={() => {
          handleVisibilityFilter("SHOW_ACTIVE");
        }}
      >
        Show Active
      </button>
      <button
        onClick={() => {
          handleVisibilityFilter("SHOW_COMPLETED");
        }}
      >
        Show Completed
      </button>

    </div>
  );
};
const mapStateToProp = (state)=>({
   VisibilityFilter:state.VisibilityFilter
}
)
export default connect(mapStateToProp)(VisibilityFilter);
