import React from "react";
import { connect } from "react-redux";
import "../App.css";
import { getVisibleTodos } from "../redux/seletors";
const VisibilityFilter = ({ VisibilityFilter, dispatch }) => {
  console.log("Visibility filter :",VisibilityFilter);
  const handleVisibilityFilter = (filter) => {
    dispatch({ type: "SET_VISIBILITY_FIlTER", payload: filter });
  };
  return (
    <div>
    <h1>Visibility Filter</h1>
    <button onClick={() => handleVisibilityFilter('SHOW_ALL')}>Show All</button>
    <button onClick={() => handleVisibilityFilter('SHOW_ACTIVE')}>Show Active</button>
    <button onClick={() => handleVisibilityFilter('SHOW_COMPLETED')}>Show Completed</button>

    {VisibilityFilter.map((todo) => (
      <div key={todo.id}>
        {todo.completed ? `${todo.inputData} (completed)` : `${todo.inputData} (not completed)`}
      </div>
    ))}
  </div>
  );
};
const mapStateToProp = (state) => ({
  VisibilityFilter: getVisibleTodos(state),
});
export default connect(mapStateToProp)(VisibilityFilter);
