import React from "react";
import { connect } from "react-redux";
import { getVisibleTodos } from "../redux/seletors";

const VisibilityFilter = ({ visibilityFilter, dispatch }) => {
  const handleVisibilityFilter = (filter) => {
    dispatch({ type: "SET_VISIBILITY_FIlTER", payload:filter });
  };
  return (
    <div>
      <h1>Visibility Filter</h1>
      <button
        onClick={() => {
          handleVisibilityFilter("SHOW_ALL");
        }}
      >
        Show All
      </button>
      <button
        onClick={() => {
          handleVisibilityFilter("SHOW_ACTIVE");
        }}
      >
        Show Active
      </button>
      <button
        onClick={() => {
          handleVisibilityFilter("SHOW_COMPLETED");
        }}
      >
        Show Completed
      </button>
    </div>
  );
};

const mapStateToProps = (state) => ({
    visibilityFilter: getVisibleTodos(state)
});

export default connect(mapStateToProps)(VisibilityFilter);
