// selectors.js

export const getVisibleTodos = (store, visibilityFilter) => {
  const allTodos = getTodos(store);
  switch (visibilityFilter) {
    case "SHOW_COMPLETED":
      return allTodos.filter((todo) => todo.completed);
    case "SHOW_ACTIVE":
      return allTodos.filter((todo) => !todo.completed);
    case "SHOW_ALL":
    default:
      return allTodos;
  }
};
