import React from "react";
import { getVisibleTodos } from "../redux/seletors";
import { connect } from "react-redux";
import {taskReducer} from "../redux/reducer";
import VisibilityFilter from "./VisibilityFilter";
const TodoList = ({ todoes, dispatch }) => {
  const handleToggleTodo = (id) => {
    dispatch({ type: "toggleTodo", payload: { id } });
  };
  return (
    <div>
      <h1>Todo List</h1>
        {todoes.map(todo => (
    <div key={todo.id}> {/* Ensure todo.id is a unique identifier */}
        {todo.inputData}-{todo.completed ? 'completed' : 'Not Completed'}
    </div>
))}

    </div>
  );
};

const mapStateToProp=(state)=>({
    todoes:getVisibleTodos(state,VisibilityFilter),
})

export default connect(mapStateToProp)(TodoList);
