import React from "react";
import { getTodos } from "../redux/seletors";
import { connect } from "react-redux";

const VisibilityFilter = ({ todos }) => {
  console.log("todo :"  ,todos)
  const showAll=()=>{
    if(todos.completed){
      return todos.inputData
    }
  }
  return (
    <div>
      <h2>VISIBILITY FILTER</h2>
      <button onClick={()=>showAll}>All</button>
      <button onClick={()=>showAl}>Completed</button>
      <button onClick={()=>showAll}>Not Completed</button>

      {
        todos.map((todo) => (
          <div key={todo.id} >
            <h6>{todo.inpputData}</h6>
          </div>
        ))
      }
    </div>
  );
};


const mapStateToProps = (state) => ({
  todos: getTodos(state),
});

export default connect(mapStateToProps)(VisibilityFilter);
