// VisibilityFilter.js

import React from 'react';
import { connect } from 'react-redux';
import { setVisibilityFilter } from '../redux/actions';
import { getVisibleTodos } from '../redux/selectors';

const VisibilityFilter = ({ todos, dispatch }) => {
  const handleVisibilityFilter = (filter) => {
    dispatch(setVisibilityFilter(filter));
  };

  return (
    <div>
      <h1>Visibility Filter</h1>
      <button onClick={() => handleVisibilityFilter('SHOW_ALL')}>Show All</button>
      <button onClick={() => handleVisibilityFilter('SHOW_ACTIVE')}>Show Active</button>
      <button onClick={() => handleVisibilityFilter('SHOW_COMPLETED')}>Show Completed</button>

      {todos.map((todo) => (
        <div key={todo.id}>
          {todo.completed ? `${todo.inputData} (completed)` : `${todo.inputData} (not completed)`}
        </div>
      ))}
    </div>
  );
};

const mapStateToProps = (state) => ({
  todos: getVisibleTodos(state),
});

export default connect(mapStateToProps)(VisibilityFilter);
