export const ADD_TODO = 'ADD_TODO'
const initialState={
    byIds:{},
    allIds:[]
}
let nextTodoId=0;

export const addTodo=(content)=>({
    type:ADD_TODO,
    payload:{
        id:++nextTodoId,
        content
    }
})
const taskReducer = (state=initialState,action)=>{
    switch(action.type){
        case ADD_TODO:{
            const {id,content}=action.payload;
            console.log(id)
            console.log(content)
            return {
                ...state,
                byIds:{
                    ...state.byIds,[id]:{
                        content
                    }
                },
                allIds:[...state.allIds,id],
            }
        }
        default:
            return state
    }
}
export default taskReducer;
