const initialState = {
  byIds: {},
  allIds: [],
};

const taskReducer = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_TODO": {
      const { id, inputData } = action.payload;
      return {
        ...state,
        byIds: {
          ...state.byIds,
          [id]: { inputData },
        },
        allIds: [...state.allIds, id],
      };
    }
    case 'toggleTodo':{
      
    }
    default:
      return state;
  }
};

export default taskReducer;
