export const ADD_TODO = 'ADD_TODO'
const initialState={
    byIds:{},
    allIds:[]
}

const taskReducer = (state=initialState,action)=>{
    switch(action.type){
        case ADD_TODO:{
            const {id,title}=action.payload;
            console.log("Entered input id :" ,id)
            console.log("Entered input content :" ,title)
            return {
                ...state,
                byIds:{
                    ...state.byIds,
                    [id]:{title}
                },
                allIds:[...state.allIds,id],
            }
        }
        default:
            return state
    }
}
const rootReducer = combineReducers({
    todos: taskReducer
  });
  
  export default rootReducer;
