// import { combineReducers } from "redux";

const initialState = {
  byIds: {},
  allIds: [],
};

const taskReducer = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_TODO": {
      const { id, inputData } = action.payload;
      console.log("Entered input id :", id);
      console.log("Entered input content :", inputData);
      return {
        ...state,
        byIds: {
          ...state.byIds,
          [id]: { inputData },
        },
        allIds: [...state.allIds, id],
      };
    }
    default:
      return state;
  }
};
const rootReducer = combineReducers({
  todos: taskReducer,
});

export default rootReducer;
