import React from 'react';
import { getTodos } from '../redux/seletors';
import { connect } from 'react-redux';

const TodoList = ({ todoes,dispatch }) => {
    const handleToggleTodo=(id)=>{
        dispatch({type: 'toggleTodo', payload: {id}});
    }
    return (
        <div>
            <h1>Todo List</h1>
            {todoes.map(todo => (
                <>
                <div key={todo.id} onClick={()=>handleToggleTodo(todo.id)}>
                    {todo.inputData}-{todo.completed?'completed':'Not Completed'}
                </div>
                </>
            ))}
        </div>
    );
};
const mapStateToProp=(state)=>({
    todoes:getTodos(state)
})

// const mapDispatchToProps={
//     dispatchToggleTodo : toggleTodo
// }

export default connect(mapStateToProp)(TodoList);
