// TodoList.js

import React from 'react';
import { connect } from 'react-redux';
import { getVisibleTodos } from '../redux/selectors';

const TodoList = ({ todos }) => {
  return (
    <div>
      <h1>Todo List</h1>
      {todos.map(todo => (
        <div key={todo.id}>
          {todo.inputData}-{todo.completed ? 'completed' : 'Not Completed'}
        </div>
      ))}
    </div>
  );
};

const mapStateToProps = (state) => ({
  todos: getVisibleTodos(state, state.taskReducer.visibilityFilter) // Pass visibility filter
});

export default connect(mapStateToProps)(TodoList);
